import type { MessageProp } from "@/components/message/message.tsx";
import User from "@/components/user.tsx";
import type { VerityMessageType } from "@/schema";

type VerifyMessageProps = MessageProp<VerityMessageType>;

export interface VerityMessageEntity {
	msg: {
		"@_fromusername": "wxid";
		"@_fromnickname": "nickname";
		"@_fullpy": string;
		"@_shortpy": string;
		"@_encryptusername": string;
		"@_content": string; // eg. 我是...
		"@_sign": string; // bio
		"@_imagestatus": "3";
		"@_scene": "17";
		"@_country": string;
		"@_province": string;
		"@_city": string;
		"@_percard": "1";
		"@_sex": "1";
		"@_alias": string; // user_id;
		"@_weibo": "";
		"@_albumflag": "0";
		"@_albumstyle": "0";
		"@_albumbgimgid": string;
		"@_snsflag": "305";
		"@_snsbgimgid": "http://.../0";
		"@_snsbgobjectid": string;
		"@_mhash": string;
		"@_mfullhash": string;
		"@_bigheadimgurl": "http://.../0";
		"@_smallheadimgurl": "http://.../132";
		"@_ticket": "v4_@stranger";
		"@_opcode": "2";
		"@_googlecontact": "";
		"@_qrticket": "";
		"@_chatroomusername": "";
		"@_sourceusername": "wxid";
		"@_sourcenickname": "nickname";
		"@_sharecardusername": "wxid";
		"@_sharecardnickname": "nickname";
		"@_cardversion": "0";
		"@_extflag": "0";
	};
}

export default function VerityMessage({
	message,
	variant = "default",
	...props
}: VerifyMessageProps) {
	if (variant === "default") {
		return <VerifyMessageDefault message={message} {...props} />;
	}
}

function VerifyMessageDefault({
	message,
	...props
}: Omit<VerifyMessageProps, "variant">) {
	return (
		<div className={"flex"}>
			<User.Photo user={message.from} variant="default" />
			<div
				className="max-w-80 flex items-center p-2.5 pr-3 rounded-lg bg-white"
				{...props}
			>
				{message.message_entity.msg["@_fromnickname"]}:{" "}
				{message.message_entity.msg["@_content"]}
			</div>
		</div>
	);
}
